mapper.update:
UPDATE garbage_category
SET gc_name=#{gcName}
WHERE gc_id=#{gcId}

mapper.select:
SELECT g.garbage_id,g.garbage_name,gc.gc_id,gc.gc_name,gc.gc_type FROM garbage g LEFT JOIN garbage_garbage_category_relation gcr ON g.garbage_id=gcr.gcr_garbage_id LEFT JOIN garbage_category gc ON gc.gc_id=gcr.gcr_gc_id
WHERE g.garbage_name LIKE concat('%',#{name},'%')
WHERE gc.gc_name=#{category}
ORDER BY RAND() LIMIT #{num}

mapper.update:
UPDATE garbage
SET garbage_name=#{garbageName}
WHERE garbage_id=#{garbageId}

UPDATE garbage_garbage_category_relation
SET gcr_gc_id=#{gcId}
WHERE gcr_garbage_id=#{garbageId}

mapper.delete:
DELETE FROM user_garbage_relation
WHERE ugr_user_id=#{userId}
AND ugr_garbage_id=#{garbageId}
